import React, {useEffect, useState} from "react";
import {getBookDetailsByID} from "../services/openlib-services";


const COVER_SOURCE = "https://covers.openlibrary.org/b/id"
const ActivityComponent = ({review, user}) => {

    const [book, setBook] = useState(null);

    const fetchBook = async () => {
        const bookDetails = await getBookDetailsByID(review.bid);
        setBook(bookDetails);
    }

    useEffect(() => {
        fetchBook();
    }, [review.bid])

    return(
        <>
            {book &&
                <div className="list-group">
                    <div className="list-group-item">
                        <img className="rounded mx-auto d-block" src={`${COVER_SOURCE}/${book.covers[0]}-M.jpg`}/>
                        <h4 className="text-center">{book.title}</h4>
                    </div>
                    <div className="list-group-item">
                        <div className="row">
                            <div className="col-2">
                                <img height={48} width={48} className="rounded-circle" src={user.profilePic}/>
                            </div>
                            <div className="col-10">
                                Check out what <b>{user.username}</b> has to say about it!
                            </div>
                        </div>
                    </div>

                </div>
            }



        </>
    )


}

export default ActivityComponent;
