import React, {useEffect, useState} from "react";
import {useSelector} from "react-redux";
import {useParams} from "react-router-dom";
import {followUser, getFollowers, getRecentFollowedReview, getTrendingReview} from "../services/follow-services";
import {findUserByID} from "../services/users-services.js";
import ReviewsComponent from "../reviews/reviews-component";
import {findRecentReview} from "../services/reviews-services";
import {getBookDetailsByID} from "../services/openlib-services";
import ActivityComponent from "./activity-component";

const COVER_SOURCE = "https://covers.openlibrary.org/b/id"

const Trending = () => {
    const {currentUser} = useSelector((state) => state.userData);
    const [highlight, setHighlight] = useState(null);


    const fetchHighlightedReview = async () => {
        const reviewData = await getTrendingReview(currentUser);
        setHighlight(reviewData)

    }

    useEffect(() => {
        fetchHighlightedReview()
    }, [currentUser])

    return(
        <>
            {currentUser ? <h4>People You Follow</h4> : <h5>People Are Talking About</h5>}

            {highlight &&
                <div className="list-group">
                    <div className="list-group-item">
                        <img className="rounded mx-auto d-block" src={`${COVER_SOURCE}/${highlight.book.cover}-M.jpg`}/>
                        <h4 className="text-center">{highlight.book.title}</h4>
                    </div>
                    <div className="list-group-item">
                        <div className="row">
                            <div className="col-2">
                                <img height={48} width={48} className="rounded-circle" src={highlight.user.profilePic}/>
                            </div>
                            <div className="col-10">
                                Check out what <b>{highlight.user.username}</b> has to say about it!
                            </div>
                        </div>
                    </div>

                </div>
            }

        </>


    )
}

export default Trending;

