import {useSelector} from "react-redux";
import React, {useState} from "react";

const AdminComponent = ({user}) => {

    const {currentUser} = useSelector((state) => state.userData);
    const [role, setRole] = useState(user.role);
    const [isBuyer, setIsBuyer] = useState(user.isBuyer);
    const [isEditing, setIsEditing] = useState(false);

    return(
        <>
            <h2>User Information</h2>
            <select onChange={(e) => setRole(e.target.value)} className="border-0 rounded bg-light text-secondary" name="role" id="role">
                <option selected value="guest">Guest</option>
                <option value="user">User</option>
                <option value="admin">Administrator</option>
            </select>

            <select onChange={(e) => setIsBuyer(e.target.value === 'true')} className="border-0 rounded bg-light text-secondary" name="role" id="role">
                <option selected value="true">Buyer</option>
                <option value="false">Seller</option>
            </select>


        </>
    )

}

export default AdminComponent;
