import {useParams} from "react-router-dom";
import {getBookDetailsByID} from "../services/openlib-services";
import {useSelector} from "react-redux"
import React, {useEffect, useState} from "react";
import {findSalesByBookID, findSalesByUserID} from "../services/sales-services";
const SalesComponent = ({id, isUserID}) => {

    const {currentUser} = useSelector((state) => state.userData);
    const [sales, setSales] = useState([]);
    const [isSelling, setIsSelling] = useState(false);

//!followers.find((follow) => follow.follower._id === currentUser._id)
    const fetchSales = async () => {
        if(isUserID){
            const userSales = await findSalesByUserID(id);
            setSales(userSales);
        }
        else{
            const bookSales = await findSalesByBookID(id);
            setSales(bookSales);
        }
    }

    useEffect(() => {
        fetchSales();
    }, [])
//{currentUser.isBuyer ? <h6>hi</h6> : <h6>Bye</h6> }
    return(
        <>
            {isUserID ? <h6>Your Sales</h6>:<h6>Sales For Book</h6> }
            {currentUser && !currentUser.isBuyer && !sales.find((sale) => sale.uid === currentUser._id) && !isSelling && 
                <button className="btn btn-outline-success m-2" onClick={() => setIsSelling(true)}>Sell something!</button> }

            <div style={{"max-height": "clamp(10em, 10vh, 10px)", "overflow": "auto"}}>
                <table className="table table-hover table-responsive">
                    <thead>
                    <tr>
                        <th scope="col">Seller</th>
                        <th scope="col">Book</th>
                        <th scope="col">Stock</th>
                        <th scope="col">Price</th>
                        <th scope="col">#</th>
                    </tr>
                    </thead>
                    <tbody>
                    <tr>
                        <th scope="row">1</th>
                        <td>Mark</td>
                        <td>Otto</td>
                        <td>@mdo</td>
                    </tr>
                    <tr>
                        <th scope="row">2</th>
                        <td>Jacob</td>
                        <td>Thornton</td>
                        <td>@fat</td>
                    </tr>
                    <tr>
                        <th scope="row">3</th>
                        <td colSpan="2">Larry the Bird</td>
                        <td>@twitter</td>
                    </tr>
                    </tbody>
                </table>
            </div>

        </>
    )

}

export default SalesComponent;
