import {createAsyncThunk}
    from "@reduxjs/toolkit"
import * as service
    from "./users-services.js"

export const loginThunk = createAsyncThunk(
    "user/login", async (credentials) => {
        const user = await service.login(credentials);
        return user;
    }
);

export const registerThunk = createAsyncThunk(
    "user/register", async (credentials) => {
        const user = await service.register(credentials);
        return user;
    }
);

export const findUserByIDThunk = createAsyncThunk(
    "user/profile", async (uid) => {
        const user = await service.findUserByID(uid);
        return user;
    }
);

export const profileThunk = createAsyncThunk(
    "user/profile", async (uid) => {
        const user = await service.profile(uid);
        return user;
    }
);
