import axios from 'axios';
const API_BASE = 'http://localhost:4000/api'
const SALES_API = `${API_BASE}/sales`;

const api = axios.create({withCredentials: true})


export const findSaleBySaleID = async (sid) => {

    const response = await api.get(`${SALES_API}/sales/${sid}`);
    const sales = response.data;
    return sales;
}

export const findSalesByUserID = async (uid) => {

    const response = await api.get(`${SALES_API}/sales/user/${uid}`);
    const sales = response.data;
    return sales;
}

export const findSalesByBookID = async (bid) => {

    const response = await api.get(`${SALES_API}/sales/book/${bid}`);
    const sales = response.data;
    return sales;
}

export const createSale = async (sale) => {
    const response = await api.post(`${SALES_API}/sales`, sale);
    return response.data;
}


export const updateSale = async (sale) => {
    const response = await api.put(`${SALES_API}/sales`, sale);
    return response.data;
}

export const deleteSale = async (sid) => {
    const response = await api.delete(`${SALES_API}/sales/${sid}`);
    return response.data;
}
