import {loginThunk} from "../services/users-thunks";
import {Link, useParams} from "react-router-dom";
import React, {useEffect, useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import {getBookDetailsByID, getRecommended, getTrending, searchByTitle} from "../services/openlib-services";
import {findRecentReviewByUserID} from "../services/reviews-services";


const COVER_SOURCE = "https://covers.openlibrary.org/b/id"

const Explore = () => {
    const {currentUser} = useSelector(state => state.userData)
    const [recommended, setRecommended] = useState([]);
    const dispatch = useDispatch();

    const fetchRecommended = async () => {
        if(currentUser){
            var response = await getRecommended();
            if(!response.length > 0){
                response = await getTrending();
            }
            setRecommended(response);
        }
        else{
            const response = await getTrending();
            setRecommended(response);
        }

    }
    useEffect(() => {
        fetchRecommended();
    }, [currentUser]);

    return(
        <div className="border-start">
            {currentUser ? <h2>Recommended For You</h2> : <h2>Trending Topics</h2>}
            {recommended.map( (recList) =>
                <>
                    <h3>{recList.subject.charAt(0).toUpperCase() + recList.subject.slice(1)}</h3>
                    <div className="table-responsive">
                        <table className="table">
                            <tbody>
                            <tr>
                            {recList.books.map((book) =>
                                    <td>
                                        <Link to={`/details${book.key}`} className="text-decoration-none text-dark">
                                        <img src={book.cover_id ? `${COVER_SOURCE}/${book.cover_id}-M.jpg` : "https://openlibrary.org/images/icons/avatar_book.png"}/>
                                        <h3>{book.title}</h3>
                                        </Link>
                                    </td>

                            )}
                            </tr>
                            </tbody>
                        </table>
                    </div>
                </>

            )}


        </div>


    )
}

export default Explore;

