import React, {useEffect, useState} from "react";
import {Link} from "react-router-dom";
import {useSelector} from "react-redux";
import {getHighlightedReview} from "../services/follow-services";
import ReviewItemComponent from "../reviews/review-item-component";

const COVER_SOURCE = "https://covers.openlibrary.org/b/id"

const Highlight = () => {
    const {currentUser} = useSelector((state) => state.userData);
    const [highlight, setHighlight] = useState(null);


    const fetchHighlightedReview = async () => {
        const reviewData = await getHighlightedReview(currentUser);
        setHighlight(reviewData)

    }

    useEffect(() => {
        fetchHighlightedReview()
    }, [currentUser])

    return(
        <>


            {highlight &&
                <div className="list-group">
                    <div className="list-group-item">{currentUser ? <h4>People You Follow Are Reading</h4> : <h4>People Are Talking About</h4>}</div>
                    <div className="list-group-item">
                        <Link to={`/details/works/${highlight.review.bid}`} className="text-decoration-none text-dark">
                        <img className="rounded mx-auto d-block mb-2" src={`${COVER_SOURCE}/${highlight.book.cover}-M.jpg`}/>
                        <h4 className="text-center"><i>{highlight.book.title}</i></h4>
                        </Link>
                    </div>

                    <div className="list-group-item bg-light flex-column align-items-start">
                        <div className="row">
                            <div className="col-3">
                                    <img className="rounded-circle m-2" src={`${highlight.user.profilePic}`} width={72} height={72}/>

                            </div>
                            <div className="col-10">
                                <div className="d-flex w-100 justify-content-between">

                                    <h5 className="mb-1">
                                        <b><i><Link to={`/details/works/${highlight.review.bid}`} className="text-decoration-none text-dark">Click here to see !</Link></i></b>
                                    </h5>
                                    <small><i className="bi bi-three-dots"></i></small>
                                </div>
                                <p className="mb-1">{highlight.review.text}</p>
                                <small>
                                    {
                                        new Date(highlight.review.reviewDate.slice(0, 10)).toLocaleDateString('en-us', {year:"numeric", month:"short", day:"numeric"})
                                    }
                                </small>
                            </div>

                        </div>
                    </div>

                    <div className="list-group-item">
                        <div className="row">

                                <div className="col-2">
                                    <Link to={`/profile/${highlight.user._id}`}>
                                        <img height={48} width={48} className="rounded-circle" src={highlight.user.profilePic}/>
                                    </Link>
                                </div>

                            <div className="col-10">
                                Check out what <Link className="text-decoration-none text-dark" to={`/profile/${highlight.user._id}`}><b>{highlight.user.username}</b></Link> has to say about it!
                            </div>
                        </div>
                    </div>

                </div>
            }

        </>


    )
}

export default Highlight;
