import {useParams} from "react-router-dom";
import {getAuthorDetailsByID, getBookDetailsByID} from "../services/openlib-services";
import {profile} from "../services/users-services";
import React, {useEffect, useState} from "react";

const COVER_SOURCE = "https://covers.openlibrary.org/b/id"


const ResultDetailed = () => {
    const {key} = useParams();

    const [details, setDetails] = useState({});
    const [authors, setAuthors] = useState([]);
    const fetchDetails = async () => {
        const bookDetails = await getBookDetailsByID(key);
        setDetails(bookDetails);
        const authors = bookDetails.authors;
        for(let i =0; i < authors.length; i++){
            const author = authors[i];
            const authorPath = author["author"]["key"].split('/')
            const authorKey = authorPath[2];
            console.log(authorKey);
            const authorDetails = await getAuthorDetailsByID(authorKey)
            setAuthors([...authors, authorDetails])
        }

    }
    useEffect(() => {
        fetchDetails();
    }, []);



    return(
        <>
            <h2>Details</h2>
            <div className="row">
                <div className="table-responsive">
                    <table className="table">
                        <tbody>
                        <tr>
                            {details.covers ? details.covers.map(cover =>
                                <td>
                                    <img src={`${COVER_SOURCE}/${cover}-M.jpg`}/>
                                </td>) : <td><img src="https://openlibrary.org/images/icons/avatar_book.png"/></td>}
                        </tr>
                        </tbody>

                    </table>
                </div>
            </div>
            <div className="row">
                <h3>{details.title}</h3>
                <h4>Authors:</h4>


                <h5>JK Rowling</h5>
                <h5>Jack Thorne</h5>
            </div>
            <div>
                <h4>Description</h4>
                {details.description}
            </div>
            <div>
                {JSON.stringify(details)}
            </div>



        </>
    )
}

export default ResultDetailed;

