import React, {useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import {useNavigate, Link} from "react-router-dom"
import SearchBarComponent from "./search-bar-component";
import {logoutThunk} from "../services/users-thunks";


const Navigation = () => {
    const {currentUser} = useSelector(state => state.userData)
    const navigate = useNavigate();
    const dispatch = useDispatch();

    const logout = () => {
        dispatch(logoutThunk())
    }

    return(
        <div className="row flex-container border-bottom m-4">

            <nav className="navbar border-bottom navbar-expand-lg navbar-light rounded bg-secondary ms-4">
                <Link className="col-2" to={"/"}>
                    <img width={96} height={96}
                         className="float-end rounded-circle"
                         src="/images/logoPic.jpg"/>
                </Link>
                    <select className="col-1 border-0 bg-light text-secondary" name="category" id="category">
                        <option value="title">Title</option>
                        <option value="author">Author</option>
                        <option value="subject">Subject</option>
                    </select>
                    <form className="form-inline my-2 my-lg-0">
                        <input className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search"/>
                            <button className="btn btn-outline-success my-2 my-sm-0">Search</button>
                    </form>
            </nav>

            <div className="col-4 mx-auto d-block">
                <button onClick={() => navigate("/")}>Home</button>
            </div>
            <div className="col-4 mx-auto d-block">
                <SearchBarComponent/>
            </div>
            <div className="col-3 mx-auto d-block">
                {currentUser ? <button onClick={() => logout()}>Sign Out</button> : <button onClick={() => navigate(`/login`)}>Login</button>}
            </div>
        </div>
    )
}

export default Navigation;
