import {useSelector} from "react-redux";
import React, {useState} from "react";

const AdminComponent = ({user}) => {

    const {currentUser} = useSelector((state) => state.userData);
    const [role, setRole] = useState("guest");
    const [isBuyer, setIsBuyer] = useState(true);
    const [isEditing, setIsEditing] = useState(false);

    const submit = (isSaved) => {
        if(isSaved){

        }
        else{
            setIsEditing(false);
            setRole("guest");
            setIsBuyer(true);
        }
    }

    return(
        <>
            <h2>User Information</h2>
            {isEditing ?
                <>
                <div className="mb-4">
                    <select onChange={(e) => setIsBuyer(e.target.value === 'true')} className="rounded bg-light text-secondary form-control-lg" name="role" id="role">
                        <option selected value="true">Buyer</option>
                        <option value="false">Seller</option>
                    </select>
                </div>
                <div className="mb-4">
                    <select onChange={(e) => setRole(e.target.value)} className="rounded bg-light text-secondary form-control-lg" name="role" id="role">
                        <option selected value="guest">Guest</option>
                        <option value="user">User</option>
                        <option value="admin">Administrator</option>
                    </select>
                </div>

                <button className="btn btn-outline-primary" onClick={() => submit(true)}>Save</button>
                <button className="btn btn-outline-danger ms-4" onClick={() => submit(false)}>Cancel</button>
                </>
                :
                <button className="btn btn-outline-success" onClick={() => setIsEditing(true)}>Edit</button>

            }





        </>
    )

}

export default AdminComponent;
