import axios from 'axios';
const API_BASE = 'http://localhost:4000/api'
const USERS_API = `${API_BASE}/users`;

const api = axios.create({withCredentials: true})


export const login = async ({ username, password }) => {
    const response = await api.post(`${USERS_API}/login`, {
        username,
        password,
    });
    const user = response.data;
    return user;
};

export const register = async ({username, password}) => {
    const response = await api.post(`${USERS_API}/register`,
        {username, password})
    const user = response.data;
    return user;
}

export const profile = async ({uid}) => {
    const response = api.get(`${USERS_API}/profile/${uid}`);
    const profile = response.data;
    console.log(profile);
    console.log(`${USERS_API}/profile/${uid}`)
    return profile;
}
