import React, {useEffect, useState} from "react";
import {useSelector} from "react-redux";
import {useParams} from "react-router-dom";
import {followUser, getFollowers, getRecentFollowedReview} from "../services/follow-services";
import {findUserByID} from "../services/users-services.js";
import ReviewsComponent from "../reviews/reviews-component";
import {findRecentReview} from "../services/reviews-services";
import {getBookDetailsByID} from "../services/openlib-services";
import ActivityComponent from "./activity-component";

const COVER_SOURCE = "https://covers.openlibrary.org/b/id"

const Trending = () => {
    const {currentUser} = useSelector((state) => state.userData);
    const [review, setReview] = useState(null);
    const [user, setUser] = useState(null);
    const [book, setBook] = useState(null);

    const fetchBook = async () => {
        const bookDetails = await getBookDetailsByID(review.bid);
        setBook(bookDetails);
    }

    const fetchHighlightedReview = async () => {
        if(currentUser){
            const response = await getRecentFollowedReview(currentUser._id);
            setReview(response.review[0]);
            setUser(response.follow.followed);
        }
        else{
            const review = await findRecentReview();
            const user = await findUserByID(review[0].uid)
            setReview(review[0]);
            setUser(user);
        }
        fetchBook();

    }

    useEffect(() => {
        fetchHighlightedReview()
    }, [currentUser])

    return(
        <>
            {currentUser ? <h4>People You Follow</h4> : <h5>People Are Talking About</h5>}

            {book &&
                <div className="list-group">
                    <div className="list-group-item">
                        <img className="rounded mx-auto d-block" src={`${COVER_SOURCE}/${book.covers[0]}-M.jpg`}/>
                        <h4 className="text-center">{book.title}</h4>
                    </div>
                    <div className="list-group-item">
                        <div className="row">
                            <div className="col-2">
                                <img height={48} width={48} className="rounded-circle" src={user.profilePic}/>
                            </div>
                            <div className="col-10">
                                Check out what <b>{user.username}</b> has to say about it!
                            </div>
                        </div>
                    </div>

                </div>
            }

        </>


    )
}

export default Trending;

