import {useParams} from "react-router-dom";
import {getAuthorDetailsByID, getBookDetailsByID} from "../services/openlib-services";
import {createReview, findReviewsByUserID} from "../services/reviews-services";
import {useSelector} from "react-redux"
import React, {useEffect, useState} from "react";
import AuthorComponent from "./author-component";

const COVER_SOURCE = "https://covers.openlibrary.org/b/id"


const ResultDetailed = () => {
    const {key} = useParams();
    const {currentUser} = useSelector(state=> state.userData)
    const [review, setReview] = useState([])
    const [allReviews, setAllReviews] = useState([]);
    const [details, setDetails] = useState({});
    const postReview = async () => {
        const newReview = await createReview({bid:key, text:review})
    }
    const fetchDetails = async () => {
        const bookDetails = await getBookDetailsByID(key);
        setDetails(bookDetails);

    }
    const fetchReviews = async () => {
        //THIS ONLY WORKS BECAUSE IM LOGGED IN RN, OTHERWISE IT DOES NOT
        //FIX THISSSSS LATERRRR
        console.log("hi")
        const response = await findReviewsByUserID(currentUser._id);
        setAllReviews(response);
    }
    useEffect(() => {
        fetchDetails();
        fetchReviews();

    }, []);



    return(
        <>
            <h2>Details</h2>
            <div className="row">
                <div className="table-responsive">
                    <table className="table">
                        <tbody>
                        <tr>
                            {details.covers ? details.covers.map(cover =>
                                <td>
                                    <img src={`${COVER_SOURCE}/${cover}-M.jpg`}/>
                                </td>) : <td><img src="https://openlibrary.org/images/icons/avatar_book.png"/></td>}
                        </tr>
                        </tbody>

                    </table>
                </div>
            </div>
            <div className="row">
                <h3>{details.title}</h3>
                <h4>Authors:</h4>

                {details.authors &&
                    details.authors.map((author) => <AuthorComponent author={author}/>)
                }
            </div>
            <div>
                <h4>Description</h4>
                {details.description ? details.description : "No description available."}
            </div>
            <div>
                <h4>Tags</h4>
                {details.subjects ? details.subjects.slice(0,5).map(subject => <button className="btn btn-secondary">{subject}</button>)
                : "no tags"}
            </div>
            <h3>Reviews</h3>
            {currentUser &&
                <div>
                <textarea className="form-control" onChange={(e) => setReview(e.target.value)}></textarea>
                    <button onClick={() => postReview()}>Post Review</button>
                </div>
            }
            {
             allReviews.map(review => <li>{review}</li>)
            }




        </>
    )
}

export default ResultDetailed;

