import {useSelector} from "react-redux"
import React, {useEffect, useState} from "react";
import {Link} from "react-router-dom";
import {findUserByID} from "../services/users-services";
import {findSalesByBookID, findSalesByUserID, createSale} from "../services/sales-services";

const SalesComponentItem = ({sale}) => {

    const [user, setUser] = useState(null)

    const fetchUser = async () => {
        const user = await findUserByID(sale.uid);
        setUser(user);
    }

    useEffect(() => {
        fetchUser();
    })

    return(
        <tr>
            <th scope="row">{user &&
                                user.username}</th>
            <td>{sale.bid}</td>
            <td>{sale.quantity}</td>
            <td>{sale.price}</td>
            <td><button className="btn btn-outline-primary m-2">Buy</button></td>
        </tr>
    )
}

export default SalesComponentItem;
