import {useSelector} from "react-redux"
import React, {useEffect, useState} from "react";
import {Link} from "react-router-dom";
import {findUserByID} from "../services/users-services";
import {findSalesByBookID, findSalesByUserID, createSale} from "../services/sales-services";
import {getBookDetailsByID} from "../services/openlib-services";

const SalesComponentItem = ({sale}) => {
    const {currentUser} = useSelector((state) => state.userData);
    const [user, setUser] = useState(null)
    const [book, setBook] = useState(null)
    const [quantity, setQuantity] = useState(sale.quantity);
    const [price, setPrice] = useState(sale.price);
    const [isValidQuantity, setIsValidQuantity] = useState('is-invalid');
    const [isValidPrice, setIsValidPrice] = useState('is-invalid');
    const [isEditing, setIsEditing] = useState(false);

    const updateQuantity = (input) => {
        setQuantity(input)
        if(input < 1){
            setIsValidQuantity('is-invalid')
        }
        else{
            setIsValidQuantity('is-valid')
        }
    }

    const updatePrice = (input) => {
        setPrice(input)
        if(input <= 0){
            setIsValidPrice('is-invalid')
        }
        else{
            setIsValidPrice('is-valid')
        }
    }


    const fetchUser = async () => {
        const user = await findUserByID(sale.uid);
        setUser(user);
    }

    const fetchBook = async () => {
        const book = await getBookDetailsByID(sale.bid);
        setBook(book);
    }

    useEffect(() => {
        fetchUser();
        fetchBook();
    }, [])

    return(
        <tr>
            <th scope="row">{user &&
                                user.username}</th>
            <td><i>{book &&
                    book.title}</i></td>
            <td>{sale &&
                sale.quantity}</td>
            <td>${isEditing ?
                <input value={quantity} onChange={(e) => updateQuantity(Math.round(Number(e.target.value)))}
                       className={`form-control ${isValidQuantity}`}
                       min={0} type="number" id="quantity" name="quantity"/>
                        :
                sale.price}</td>
            <td>
                {currentUser && user && currentUser._id !== user._id &&
                    <button className="btn btn-outline-primary m-2">Buy</button>
                }
                {currentUser && user && currentUser._id === user._id &&
                    <button className="btn btn-outline-success m-2" onClick={() => setIsEditing(!isEditing)}>Edit</button>
                }
                {currentUser && user && (currentUser._id === user._id || currentUser.isAdmin) &&
                    <button className="btn btn-outline-danger m-2">Delete</button>
                }

            </td>
        </tr>
    )
}

export default SalesComponentItem;
