import {loginThunk} from "../services/users-thunks";
import React, {useEffect, useState} from "react";
import {useDispatch} from "react-redux";


const ProfileSideComponent = (user) => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const dispatch = useDispatch();

    if(user !== null) {
        return (
            <div className="list-group">
                <div className="list-group-item">
                    <img width={110} height={110} className="m-3 rounded-circle z-index-2" src={user.profilePic}/>
                </div>
                <div className="list-group-item">
                    <div className="mt-2"><b>{user.username}</b></div>
                    <div>I love books, reading, and all kinds of things!</div>
                </div>
            </div>


        )
    }
    else{
        return(
            <div className="list-group">
                <div className="list-group-item">
                    <div>
                        <label htmlFor="username">Username:</label>
                        <input id="username"
                               type="text"
                               value={username}
                               onChange={(e) => setUsername(e.target.value)}/>
                        <label htmlFor="password">Password:</label>
                        <input id="password"
                               type="password"
                               value={password}
                               onChange={(e) => setPassword(e.target.value)}/>
                    </div>
                    <button onClick={() => dispatch(loginThunk({username, password}))}>Login</button>
                </div>
                <div className="list-group-item">
                    <div className="mt-2"><b>New user? Sign up here</b></div>
                </div>

            </div>
        )
    }
};
export default ProfileSideComponent;
