import {useParams, Link, useNavigate} from "react-router-dom";
const FollowersComponent = ({followers}) => {
    return(
        <>
            {followers &&
                <>

            <h2>Followers</h2>
            <div className="list-group col-8">
                {followers.map((follow) =>
                    <div className="list-group-item">
                        <div className="col-3">
                            <img width={48} height={48} className="rounded-circle" src={follow.follower.profilePic}/>
                        </div>
                        <div className="col-9">
                            <Link className="text-decoration-none text-dark" to={`/profile/${follow.follower._id}`}><b>{follow.follower.username}</b></Link>
                        </div>
                    </div>
                    )
                }

            </div>
            </>}
        </>
    )
}

export default FollowersComponent;
