import {loginThunk} from "../services/users-thunks";
import React, {useEffect, useState} from "react";
import {useDispatch, useSelector} from "react-redux";


const ProfileSideComponent = (user) => {
    //const {currentUser} = useSelector(state => state.userData)
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const dispatch = useDispatch();

    console.log(user);



    if(user) {
        return (
            <>
                <div className="card">
                    <img className="w-100 card-img-top" src={user.coverPic}/>

                    <div className="card-body">
                        <div className="position-relative" style={{"bottom": "60px"}}>
                            <img width={96} height={96}
                                 className="rounded-circle"
                                 src={user.profilePic}/>
                            <h3>{user.username}</h3>
                            <p>{user.bio}</p>
                        </div>
                        <i className="bi bi-calendar-event me-2"></i>
                        {
                            new Date(user.joinDate.slice(0, 10)).toLocaleDateString('en-us', {year:"numeric", month:"short", day:"numeric"})
                        }
                    </div>
                </div>
            </>


        )
    }
    else{
        return(
            <div className="list-group">
                <div className="list-group-item">
                    <div>
                        <label htmlFor="username">Username:</label>
                        <input id="username"
                               type="text"
                               value={username}
                               onChange={(e) => setUsername(e.target.value)}/>
                        <label htmlFor="password">Password:</label>
                        <input id="password"
                               type="password"
                               value={password}
                               onChange={(e) => setPassword(e.target.value)}/>
                    </div>
                    <button onClick={() => dispatch(loginThunk({username, password}))}>Login</button>
                </div>
                <div className="list-group-item">
                    <div className="mt-2"><b>New user? Sign up here</b></div>
                </div>

            </div>
        )
    }
};
export default ProfileSideComponent;

