import React, {useEffect, useState} from "react";
import {useSelector} from "react-redux";
import {useParams} from "react-router-dom";
import {followUser, getFollowers, getRecentFollowedReview} from "../services/follow-services";
import {findUserByID} from "../services/users-services.js";
import ReviewsComponent from "../reviews/reviews-component";
import {findRecentReview} from "../services/reviews-services";
import {getBookDetailsByID} from "../services/openlib-services";

const Trending = () => {
    const {currentUser} = useSelector((state) => state.userData);
    const [highlight, setHighlight] = useState({});
    const [book, setBook] = useState({});

    const fetchHighlightedReview = async () => {
        if(currentUser){
            const highlight = await getRecentFollowedReview(currentUser._id);
            setHighlight(highlight);
        }
        else{
            const highlight = await findRecentReview();
            setHighlight(highlight);
        }
        const book = await getBookDetailsByID(highlight.review[0].bid)
        setBook(book);

    }

    useEffect(() => {
        fetchHighlightedReview()
    }, [currentUser])

    return(
        <>
            <h6>Someone You Follow Read...</h6>

            {JSON.stringify(highlight.review[0].bid)}

        </>


    )
}

export default Trending;

