import {loginThunk} from "../services/users-thunks";
import {Link, useParams} from "react-router-dom";
import React, {useEffect, useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import {getBookDetailsByID, getRecommended, searchByTitle} from "../services/openlib-services";
import {findRecentReviewByUserID} from "../services/reviews-services";


const COVER_SOURCE = "https://covers.openlibrary.org/b/id"

const Explore = () => {
    const {currentUser} = useSelector(state => state.userData)
    const [recommended, setRecommended] = useState([]);
    const dispatch = useDispatch();

    const fetchRecommended = async () => {
        if(currentUser){
            const response = await getRecommended();
            setRecommended(response);
        }

    }
    useEffect(() => {
        fetchRecommended();
    }, []);

    return(
        <div>
            <h1>You Might Also Like...</h1>
            {recommended.map( (recList) =>
                <>
                    <h3>{recList.subject.charAt(0).toUpperCase() + recList.subject.slice(1)}</h3>
                    <div className="table-responsive">
                        <table className="table">
                            <tbody>
                            <tr>
                            {recList.books.map((book) =>
                                    <td>

                                        <img src={`${COVER_SOURCE}/${book.cover_id}-M.jpg`}/>
                                        <h3>{book.title}</h3>
                                    </td>

                            )}
                            </tr>
                            </tbody>
                        </table>
                    </div>
                </>

            )}


        </div>


    )
}

export default Explore;

