import {useParams, Link, useNavigate} from "react-router-dom";
const FollowersComponent = ({followers}) => {
    return(
        <>
            {followers &&
                <>

            <h2>Followers</h2>
            <div className="list-group col-8">
                {followers.map((follow) =>
                    <div className="list-group-item">
                        <Link className="text-decoration-none text-dark" to={`/profile/${follow.follower._id}`}>
                            <div className="list-group-item border-0">
                                <img width={48} height={48} className="rounded-circle mx-auto d-block" src={follow.follower.profilePic}/>
                            </div>
                            <div className="list-group-item border-0">
                                <div className="m">{follow.follower.username}</div>
                            </div>

                        </Link>
                    </div>
                    )
                }

            </div>
            </>}
        </>
    )
}

export default FollowersComponent;
