import React, {useState} from "react";
import {findUserByUsername} from "../services/users-services";
const RegisterComponent = () => {

    const [username, setUsername] = useState("");
    const [validUsername, setValidUsername] = useState("is-invalid");

    const [password, setPassword] = useState("");
    const [validPassword, setValidPassword] = useState("is-invalid");

    const [phone, setPhone] = useState("");
    const [validPhone, setValidPhone] = useState("");

    const [email, setEmail] = useState("");
    const [birthday, setBirthday] = useState("");

    const updateUsername = (input) =>{
        setUsername(input);
        if(input.length === 0){setValidUsername("is-invalid");}
        else{setValidUsername("is-valid");}
    }
    const updatePassword = (input) =>{
        setPassword(input);
        var pattern = /^(?=.{5,})(?=.*[A-Z])(?=.*[a-z])(?=.*[0-9])(?=.*[\W])/;
        if(input.match(pattern)){
            setValidPassword("is-valid")
        }
        else{
            setValidPassword("is-invalid")
        }
    }

    const updatePhone = (input) =>{
        setPhone(input);
        var pattern = /^\d{3}-\d{3}-\d{4}$/;
        if(input.match(pattern)){
            setValidPhone("is-valid")
        }
        else{
            setValidPhone("is-invalid")
        }
    }
    const updateEmail = (input) =>{
        var pattern = /^\w+@[a-zA-Z_]+?\.[a-zA-Z]{2,3}$/;
        setUsername(input);
        console.log(username.match(pattern));
    }

    const register = () => {
        const username = username;
    }

    return(
        <>
            <form>
                <div className="form-row">
                    <div className="col-md-4 mb-3">
                        <label htmlFor="username">Username</label>
                        <input type="text" className={`form-control ${validUsername}`} id="username"
                               placeholder="Username" value={username} onChange={(e) => updateUsername(e.target.value) } required/>
                            <div className="invalid-feedback">
                                Required
                            </div>
                    </div>
                    <div className="col-md-4 mb-3">
                        <label htmlFor="password">Password</label>
                        <input type="password" className={`form-control ${validPassword}`} id="password"
                               placeholder="Password" value={password} onChange={(e) => updatePassword(e.target.value)} required/>
                            <div className="valid-feedback">
                                Looks good!
                            </div>
                        <div className="invalid-feedback">
                            Password should be at least 5 characters long and contain one uppercase letter, lowercase letter, digit, and special character.
                        </div>
                    </div>
                    <div className="col-md-4 mb-3">
                        <label htmlFor="phone">Phone Number</label>
                        <div className="input-group">
                            <div className="input-group-prepend">
                                <span className="input-group-text" id="inputGroupPrepend3">+1</span>
                            </div>
                            <input type="text" className={`form-control ${validPhone}`} id="phone"
                                   placeholder="xxx-xxx-xxxx" aria-describedby="inputGroupPrepend3" onChange={(e) => updatePhone(e.target.value)} required/>
                                <div className="invalid-feedback">
                                    Please enter a valid phone number.
                                </div>
                        </div>
                    </div>
                </div>
                <div className="form-row">
                    <div className="col-md-6 mb-3">
                        <label htmlFor="validationServer03">City</label>
                        <input type="text" className="form-control is-invalid" id="validationServer03"
                               placeholder="City" required/>
                            <div className="invalid-feedback">
                                Please provide a valid city.
                            </div>
                    </div>
                    <div className="col-md-3 mb-3">
                        <label htmlFor="validationServer04">State</label>
                        <input type="text" className="form-control is-invalid" id="validationServer04"
                               placeholder="State" required/>
                            <div className="invalid-feedback">
                                Please provide a valid state.
                            </div>
                    </div>
                    <div className="col-md-3 mb-3">
                        <label htmlFor="validationServer05">Zip</label>
                        <input type="text" className="form-control is-invalid" id="validationServer05" placeholder="Zip"
                               required/>
                            <div className="invalid-feedback">
                                Please provide a valid zip.
                            </div>
                    </div>
                </div>
                <div className="form-group">
                    <div className="form-check">
                        <input className="form-check-input is-invalid" type="checkbox" value="" id="invalidCheck3"
                               required/>
                            <label className="form-check-label" htmlFor="invalidCheck3">
                                Agree to terms and conditions
                            </label>
                            <div className="invalid-feedback">
                                You must agree before submitting.
                            </div>
                    </div>
                </div>
                <button className="btn btn-primary" type="submit">Submit form</button>
            </form>
        </>
    )
}

export default RegisterComponent;

