import React, {useEffect, useState} from "react";
import {getBookDetailsByID} from "../services/openlib-services";


const COVER_SOURCE = "https://covers.openlibrary.org/b/id"
const ActivityComponent = ({highlight}) => {

    const [book, setBook] = useState({});

    const fetchBook = async () => {
        const bookDetails = await getBookDetailsByID(highlight.review[0].bid);
        setBook(bookDetails);
    }

    useEffect(() => {
        fetchBook();
    }, [])

    return(
        <>
            <div className="list-group">
                <div className="list-group-item">
                    <img className="rounded mx-auto d-block" src={`${COVER_SOURCE}/${book.covers[0]}-M.jpg`}/>
                </div>
                <div className="list-group-item">
                    <div className="row">
                        <div className="col-2">
                            <img height={48} width={48} className="rounded-circle" src={highlight.follow.followed.profilePic}/>
                        </div>
                        <div className="col-10">
                            <b>{highlight.follow.followed.username}</b> recently read <b>{book.title}</b>
                        </div>
                    </div>
                </div>

            </div>


        </>
    )


}

export default ActivityComponent;
