import React, {useEffect, useState} from "react";
import {useSelector} from "react-redux";
import {useParams} from "react-router-dom";
import {followUser, getFollowers, getRecentFollowedReview} from "../services/follow-services";
import {findUserByID} from "../services/users-services.js";
import ReviewsComponent from "../reviews/reviews-component";
import {findRecentReview} from "../services/reviews-services";
import {getBookDetailsByID} from "../services/openlib-services";
import ActivityComponent from "./activity-component";

const Trending = () => {
    const {currentUser} = useSelector((state) => state.userData);
    const [highlight, setHighlight] = useState(null);

    const fetchHighlightedReview = async () => {
        if(currentUser){
            const response = await getRecentFollowedReview(currentUser._id);
            setHighlight({review: response.review[0], user: response.follow.followerd});
        }
        else{
            const review = await findRecentReview();
            const user = await findUserByID(review[0].uid)
            setHighlight({review:review, user:user})
        }

    }

    useEffect(() => {
        fetchHighlightedReview()
    }, [currentUser])

    return(
        <>
            {currentUser ? <h4>People You Follow</h4> : <h5>People Are Talking About</h5>}

            {review ?
            <ActivityComponent highlight={highlight}/> : <>Loading</>}

        </>


    )
}

export default Trending;

