import { createSlice } from "@reduxjs/toolkit";
import {findUsersThunk} from "../services/users-thunks";

const initialState = {
    users: [],
    loading: false
}

const usersSlice = createSlice({
    name: 'users',
    initialState,
    extraReducers: {
        [findUsersThunk.pending]:
            (state) => {
                state.loading = true
                state.tuits = []
            },
        [findUsersThunk.fulfilled]:
            (state, {payload}) => {
                state.loading = false
                state.tuits = payload
            },
        [findUsersThunk.rejected]:
            (state, action) => {
                state.loading = false
                state.error = action.error
            }
    },
});
export default usersSlice.reducer;
