import {createAsyncThunk}
    from "@reduxjs/toolkit"
import * as service
    from "./users-services.js"

export const loginThunk = createAsyncThunk(
    "user/login", async (credentials) => {
        const user = await service.login(credentials);
        return user;
    }
);
