import React, {useEffect, useState} from "react";
import {Link} from "react-router-dom";
import {useSelector} from "react-redux";
import {getHighlightedReview} from "../services/follow-services";

const COVER_SOURCE = "https://covers.openlibrary.org/b/id"

const Highlight = () => {
    const {currentUser} = useSelector((state) => state.userData);
    const [highlight, setHighlight] = useState(null);


    const fetchHighlightedReview = async () => {
        const reviewData = await getHighlightedReview(currentUser);
        setHighlight(reviewData)

    }

    useEffect(() => {
        fetchHighlightedReview()
    }, [currentUser])

    return(
        <>
            {currentUser ? <h4>People You Follow</h4> : <h5>People Are Talking About</h5>}

            {highlight &&
                <div className="list-group">
                    <div className="list-group-item">
                        <Link to={`/details/works/${highlight.review.bid}`} className="text-decoration-none text-dark">
                        <img className="rounded mx-auto d-block" src={`${COVER_SOURCE}/${highlight.book.cover}-M.jpg`}/>
                        <h4 className="text-center"><i>{highlight.book.title}</i></h4>
                        </Link>
                    </div>
                    <div className="list-group-item">
                        <div className="row">

                                <div className="col-2">
                                    <Link to={`/profile/${highlight.user._id}`}>
                                    <img height={48} width={48} className="rounded-circle" src={highlight.user.profilePic}/>
                                    </Link>
                                </div>

                            <div className="col-10">
                                Check out what <Link className="text-decoration-none text-dark" to={`/profile/${highlight.user._id}`}><b>{highlight.user.username}</b></Link> has to say about it!
                            </div>
                        </div>
                    </div>

                </div>
            }

        </>


    )
}

export default Highlight;
