import axios from 'axios';
const API_BASE = 'http://localhost:4000/api'
const FOLLOW_API = `${API_BASE}/follow`;

const api = axios.create({withCredentials: true})

export const followUser = (uid) => {
    const result = api.post(`${FOLLOW_API}/${uid}`);
    return result.data;
}
